import { useState } from "react";
import { motion } from "framer-motion";

export default function ChargingSession() {
    const [charging, setCharging] = useState<boolean>(false);
    const [energyConsumed, setEnergyConsumed] = useState<number>(0);
    const [status, setStatus] = useState<string>("Idle");

    const startCharging = () => {
        setCharging(true);
        setStatus("Charging");
        setEnergyConsumed(0);

        const interval = setInterval(() => {
            setEnergyConsumed((prev) => prev + 0.5);
        }, 1000);

        setTimeout(() => clearInterval(interval), 20000);
    };

    const stopCharging = () => {
        setCharging(false);
        setStatus("Stopped");
    };

    return (
        <div className="flex flex-col items-center space-y-6 p-6">
            <div className="w-80 text-center p-4 shadow-lg border rounded-lg bg-white">
                <div>
                    <h2 className="text-xl font-semibold">Charging Session</h2>
                    <motion.p
                        className="mt-4 text-lg"
                        animate={{ opacity: charging ? 1 : 0.6 }}
                    >
                        Status: {status}
                    </motion.p>
                    <motion.p
                        className="mt-2 text-lg"
                        animate={{ scale: charging ? 1.1 : 1 }}
                    >
                        Energy Consumed: {energyConsumed.toFixed(2)} kWh
                    </motion.p>
                </div>
            </div>
            <div className="flex space-x-4">
                <button
                    onClick={startCharging}
                    disabled={charging}
                    className="px-4 py-2 rounded bg-green-500 hover:bg-green-600 text-white"
                >
                    Start Charging
                </button>
                <button
                    onClick={stopCharging}
                    disabled={!charging}
                    className="px-4 py-2 rounded bg-red-500 hover:bg-red-600 text-white"
                >
                    Stop Charging
                </button>
            </div>
        </div>
    );
}
